import { memo, VFC, useCallback } from "react";
import {
	Flex,
	Heading,
	Box,
	Divider,
	Stack,
	Link,
	Button,
} from "@chakra-ui/react";
import { PrimaryButton } from "../atoms/button/PrimaryButton";
import { useNavigate } from "react-router";
import axios from "axios";
import { Navigate } from "react-router";

export const Login: VFC = memo(() => {
	const navigate = useNavigate();

	const onClickFtAuth = useCallback(() => {
		window.location.replace("https://api.intra.42.fr/oauth/authorize?client_id=5ec7a9808358afbd8a4a174c7d89d4ae27fc87b35477bf1065cc11387df68549&redirect_uri=http%3A%2F%2Flocalhost%3A3000%2Fhome&response_type=code")
	}, []);

	return (
		<Flex align="center" justify="center" height="100vh">
			<Box bg="white" w="sm" p={4} borderRadius="md" shadow="md">
				<Heading as="h1" size="lg" textAlign="center">
					42_transcendence
				</Heading>
				<Divider my={4} />
				<Stack spacing={6} py={4} px={10}>
					<Button
						bg="teal.400"
						color="white"
						_hover={{ opacity: 0.8 }}
					>
						<Link href="https://api.intra.42.fr/oauth/authorize?client_id=5ec7a9808358afbd8a4a174c7d89d4ae27fc87b35477bf1065cc11387df68549&redirect_uri=http%3A%2F%2Flocalhost%3A3000%2Fhome&response_type=code">
							42 Authorization
						</Link>
					</Button>
				</Stack>
			</Box>
		</Flex>
	);
});
