import { useState, useCallback } from "react";
import axios from "axios";

import { Player } from "../types/api/Player";
import { FtUser } from "../types/api/FtUser";

export const useGetPlayer = () => {
	const [player, setPlayer] = useState<Player | null>(null);

	const getPlayer = useCallback((ftUser: FtUser) => {
		axios
			.get(`http://localhost:3001/players/${ftUser.name}`)
			.then((res) => setPlayer(res.data))
			.catch(() => {
				axios
					.post(`http://localhost:3001/players`, {
						name: ftUser.name,
						imgUrl: ftUser.imgUrl,
						password: "42user",
						ftUser: true,
					})
					.then((creRes) => setPlayer(creRes.data));
			})
			.catch(() => console.log("Cannot create player"));
	}, []);

	return { getPlayer, player };
};
