import { memo, VFC, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import axios from "axios";
import { PlayerCard } from "../organisms/player/PlayerCard";
import { useGetToken } from "../../hooks/useGetToken";

export const Home: VFC = memo(() => {
	const [searchParams, setSearchParams] = useSearchParams();
	const code = searchParams.get("code");
	const accessToken = axios.post("https://api.intra.42.fr/oauth/token", {
		grant_type: "authorization_code",
		client_id:
			"5ec7a9808358afbd8a4a174c7d89d4ae27fc87b35477bf1065cc11387df68549",
		client_secret:
			"811b6f2ae3b9ccb0fb01fe47113e2189280f6daa0a4ecadf082d3c1cd96670d6",
		code: code,
		redirect_uri: "http://localhost:3000/home",
	}).data;
	//	const { getAccessToken, accessToken } = useGetToken();
	//
	//	useEffect(() => {
	//		getAccessToken(code!);
	//	}, [getAccessToken]);
	//
	if (!accessToken) {
		return (
			<PlayerCard
				imageUrl="https://source.unsplash.com/random"
				id={"aaaa"}
				name={"hogehoge"}
				win={100}
				lose={50}
				level={42}
				exp={4242}
			/>
		);
	} else {
		return (
			<PlayerCard
				imageUrl="https://source.unsplash.com/random"
				id={"aaaa"}
				name={accessToken.access_token}
				win={100}
				lose={50}
				level={42}
				exp={4242}
			/>
		);
	}
});
