import { useCallback, useState } from "react";
import axios from "axios";
import { Player } from "../types/api/Player";
import { useNavigate } from "react-router-dom";
import { useMessage } from "./useMessage";
import { useLoginPlayer } from "..//hooks/useLoginPlayer";

export const useAuth = () => {
	const navigate = useNavigate();
	const { showMessage } = useMessage();
	const { setLoginPlayer } = useLoginPlayer();

	//	const [loading, setLoading] = useState(false);

	const login = useCallback(
		(name: string, password: string) => {
			console.log({ name, password });
			axios
				.get<Player>(`http:///localhost:3001/players/${name}`)
				.then((res) => {
					console.log(res.data);
					if (res.data.password === password) {
						setLoginPlayer({ ...res.data });
						showMessage({
							title: "Sign In Successful",
							status: "success",
						});
						navigate("/home");
					} else {
						showMessage({
							title: "The Sign In attempt failed. Password is invalid.",
							status: "error",
						});
						//						setLoading(false);
					}
				})
				.catch(() => {
					showMessage({
						title: "The Sign In attempt failed. That User Name not found.",
						status: "error",
					});
					//					setLoading(false);
				});
		},
		[navigate, showMessage, setLoginPlayer]
	);
	return {
		login,
		//		loading
	};
};
