import { useCallback, useState } from "react";
import axios from "axios";
import { Player } from "../types/api/Player";
import { useNavigate, useSearchParams } from "react-router-dom";
import { useMessage } from "./useMessage";
import { useLoginPlayer } from "./useLoginPlayer";
import { use42User } from "./use42User";
import { useGetPlayer } from "./useGetPlayer";

export const useFtAuth = () => {
	const navigate = useNavigate();
	const { showMessage } = useMessage();
	const { setLoginPlayer } = useLoginPlayer();
	const { getFtUser, FtUser } = use42User();
	const { getPlayer, player } = useGetPlayer();

	const [loadingFt, setLoadingFt] = useState(false);
	const [searchParams] = useSearchParams();

	function auth42() {
		return new Promise((resolve) => {
			window.location.href =
				"https://api.intra.42.fr/oauth/authorize?client_id=5ec7a9808358afbd8a4a174c7d89d4ae27fc87b35477bf1065cc11387df68549&redirect_uri=http%3A%2F%2Flocalhost%3A3000%2Fhome&response_type=code";
			setLoadingFt(false);
		});
	}

	async function getCode() {
		await auth42();
	}

	const loginFt = useCallback(async function sample() {
		setLoadingFt(true);
		await auth42();

		//		code && getFtUser(code);
		//		FtUser && getPlayer(FtUser);
		//		player &&
		//			setLoginPlayer(player) &&
		//			showMessage({
		//				title: "42 Authorization Successful",
		//				status: "success",
		//			}) &&
		//			navigate("/home");
		setLoadingFt(false);
	}, []);
	return {
		loginFt,
		loadingFt,
	};
};
