import { useState, useCallback } from "react";
import axios from "axios";

import { Player } from "../types/api/Player";
import { FtUser } from "../types/api/FtUser";

export const useGetFtPlayer = () => {
	const [Ftplayer, setFtPlayer] = useState<Player | null>(null);

	const getFtPlayer = useCallback((ftUser: FtUser) => {
		axios
			.get(`http://localhost:3001/players/${ftUser.name}`)
			.then((res) => setFtPlayer(res.data))
			.catch(() => {
				axios
					.post(`http://localhost:3001/players`, {
						name: ftUser.name,
						imgUrl: ftUser.imgUrl,
						password: "42user",
						ftUser: true,
					})
					.then((creRes) => setFtPlayer(creRes.data));
			})
			.catch(() => console.log("Cannot create player"));
	}, []);

	return { getFtPlayer, Ftplayer };
};
