						navigate("/home");
					} else {
						//						showMessage({
						//							title: "ユーザーが見つかりません",
						//							status: "error",
						//						});
						//						setLoading(false);
					}
				})
				.catch(() => {
					//					showMessage({
					//						title: "ログインできません",
					//						status: "error",
					//					});
					//					setLoading(false);
				});
		},
		[
			navigate,
			//			showMessage,
			setLoginPlayer,
		]
	);
	return {
		login,
		//		loading
	};
};
