import { useState, useCallback } from "react";
import axios from "axios";
import { FtUser } from "../types/api/FtUser";
import { useLoginPlayer } from "./useLoginPlayer";

const getToken = (code: string) =>
	axios.post("https://api.intra.42.fr/oauth/token", {
		grant_type: "authorization_code",
		client_id:
			"5ec7a9808358afbd8a4a174c7d89d4ae27fc87b35477bf1065cc11387df68549",
		client_secret:
			"811b6f2ae3b9ccb0fb01fe47113e2189280f6daa0a4ecadf082d3c1cd96670d6",
		code: code,
		redirect_uri: "http://localhost:3000/home",
	});

const getMe = (accessToken: string) =>
	axios.get("https://api.intra.42.fr/v2/me", {
		headers: {
			Authorization: `Bearer ${accessToken}`,
		},
	});

const getPlayers = (playerName: string) =>
	axios.get(`http://localhost:3001/players/${playerName}`);

const create42Player = (playerName: string, imgUrl: string) =>
	axios.post(`http://localhost:3001/players`, {
		name: playerName,
		imgUrl: imgUrl,
		password: "42user",
		ftUser: true,
	});

export const use42User = () => {
	//	const [FtUser, setFtUser] = useState<FtUser | null>(null);
	const { loginPlayer, setLoginPlayer } = useLoginPlayer();

	const getFtUser = useCallback((code: string) => {
		//		axios
		//			.post("https://api.intra.42.fr/oauth/token", {
		//				grant_type: "authorization_code",
		//				client_id:
		//					"5ec7a9808358afbd8a4a174c7d89d4ae27fc87b35477bf1065cc11387df68549",
		//				client_secret:
		//					"811b6f2ae3b9ccb0fb01fe47113e2189280f6daa0a4ecadf082d3c1cd96670d6",
		//				code: code,
		//				redirect_uri: "http://localhost:3000/home",
		//			})
		getToken(code)
			.then((tokenRes) => {
				getMe(tokenRes.data.access_token)
					//				axios
					//					.get("https://api.intra.42.fr/v2/me", {
					//						headers: {
					//							Authorization: `Bearer ${accessToken.data.access_token}`,
					//						},
					//					})
					.then((Me) => {
						console.log("Me Pass");
						getPlayers(Me.data.login)
							.then((res) => setLoginPlayer(res.data))
							.catch(() => {
								create42Player("rtomishi", "xxx"
							//		Me.data.login,
							//		Me.data.imgUrl
								).then((res) => setLoginPlayer(res.data));
							});
						//						setFtUser({
						//							imgUrl: res.data.image_url,
						//							name: res.data.login,
						//						});
					})
					.catch(() => console.log("Not Found Me"));
			})
			.catch(() => console.log("Not Found AccessToken"));
	}, []);
	return { getFtUser };
};
