import { memo, VFC, useEffect } from "react";
import { useSearchParams } from "react-router-dom";

import { PlayerCard } from "../organisms/player/PlayerCard";
import { use42User } from "../../hooks/use42User";
import { useGetPlayer } from "../../hooks/useGetPlayer";

export const Home: VFC = memo(() => {
	const [searchParams] = useSearchParams();
	const code = searchParams.get("code");

	const { getFtUser, FtUser } = use42User();
	const { getPlayer, player } = useGetPlayer();

	useEffect(() => {
		code && getFtUser(code);
		FtUser && getPlayer(FtUser);
	}, [getFtUser, getPlayer, code, FtUser]);

	if (!FtUser) {
		return <p>This is home Page!</p>;
	} else {
		return (
			<PlayerCard
				imgUrl={FtUser.imgUrl!}
				name={FtUser.name}
				win={FtUser.win}
				lose={FtUser.lose}
				level={FtUser.level}
				exp={FtUser.exp}
			/>
		);
	}
});
